[
  {
    "question": "What are the two ways to use configuration files with ESLint?",
    "answer": "1. Configuration Files (using JavaScript, JSON, or YAML files) 2. Configuration Comments (using JavaScript comments embedded in source code)"
  },
  {
    "question": "What are the three locations where ESLint looks for configuration files?",
    "answer": "1. Custom locations specified with --config command line option 2. The current working directory where ESLint CLI is executed 3. The directory hierarchy, from current working directory up to root"
  },
  {
    "question": "What are the default file extensions ESLint searches for?",
    "answer": ".js, .mjs, and .cjs"
  },
  {
    "question": "What are the main properties that can be included in a configuration object?",
    "answer": "name, files, ignores, languageOptions, linterOptions, processor, plugins, rules, and settings"
  },
  {
    "question": "What are the three possible values for rule settings in ESLint?",
    "answer": "'off' or 0 (turn rule off), 'warn' or 1 (turn rule on as warning), 'error' or 2 (turn rule on as error)"
  },
  {
    "question": "What are the three predefined configurations provided by ESLint?",
    "answer": "eslint:recommended, eslint:all, and eslint:flat-config-recommended"
  },
  {
    "question": "How can you disable ESLint rules using configuration comments?",
    "answer": "Using /* eslint-disable */, /* eslint-enable */, /* eslint-disable-line */, or /* eslint-disable-next-line */"
  },
  {
    "question": "What is the purpose of the 'extends' property in ESLint configuration?",
    "answer": "It allows configurations to be extended by accepting arrays of configuration objects, single configuration objects, or functions that return configuration objects"
  },
  {
    "question": "What does the 'languageOptions' property configure?",
    "answer": "It configures how JavaScript is interpreted, including ecmaVersion, sourceType, globals, parser, and parserOptions"
  },
  {
    "question": "What are the key differences when migrating to Flat Config?",
    "answer": "New format is called flat config, extends works differently, overrides is replaced by multiple config objects, env is replaced by languageOptions.globals, file extensions are no longer automatically recognized, and default files pattern is more restrictive"
  }
]
